function contentCtrl(a,b,c){a.remove=function(){c.delete("/blog/archive/"+a.displaytitle).success(function(a,c){return 204==c?b.location.href="/blog":void 0})},a.getPosts=function(){c.get("/blog/archive").success(function(c){angular.element(b.document.getElementById("other-posts-loading")).remove(),c.list&&(a.otherposts=c.list),c.err&&console.log(err)})},a.getComments=function(){c.get("/blog/"+a.displaytitle+"/comments").success(function(c){angular.element(b.document.getElementById("comments-loading")).remove(),c.list&&(a.comments=c.list),c.err&&console.log(err)})}}