function carouselCtrl($scope,$http){$scope.interval=1e4,$scope.slides=[{image:"loading-haven.jpg",text:"Haven"}];var sync=function(){$http.get("/blog/carousel").success(function(data){$scope.slides=$scope.slides.concat(data.list)})};sync()}function blogThumbCtrl($scope,$http,$window,$alertService){$scope.getBlogs=function(limit,tags){var opt={params:{}};"undefined"!=typeof limit&&(opt.params.l=limit),"undefined"!=typeof tags&&(opt.params.t=tags),$http.get("/blog/archive",opt).success(function(data){return angular.element($window.document.getElementById($scope.loading)).remove(),data.list&&($scope.blogs=data.list),data.err&&data.msg?$alertService.send(data.msg):void(data.msg&&($scope.msg=data.msg))})}}