function blogListCtrl($scope,$window,$http,$alertService){$scope.remove=function(index){$http.delete("/blog/archive/"+$scope.blogs[index].displaytitle).success(function(data,status){204==status&&$scope.blogs.splice(index,1)})},$scope.getPosts=function(){$http.get("/blog/archive").success(function(data){angular.element($window.document.getElementById("blogs-loading")).remove(),data.list&&($scope.blogs=data.list),data.msg&&$alertService.send(data.msg)})},$scope.getTags=function(name){return $http.get("/blog/archive/tags",{params:{t:name}}).then(function(res){return res.data.err&&console.log(res.data.err),res.data.msg&&console.log(res.data.msg),res.data.list})},$scope.getPosts()}