function blogListCtrl($scope,$window,$http,$alertService){$scope.remove=function(index){$http.delete("/blog/archive/"+$scope.blogs[index].displaytitle).success(function(data,status){204==status&&$scope.blogs.splice(index,1)})},$scope.getPosts=function(){$http.get("/blog/archive").success(function(data){angular.element($window.document.getElementById("blogs-loading")).remove(),data.list&&($scope.blogs=data.list),data.msg&&$alertService.send(data.msg)})},$scope.getTags=function(){$http.get("/blog/archive/tags").success(function(data){data.list&&($scope.tags=data.list),data.msg&&$alertService.send(data.msg)})},$scope.getPosts(),$scope.getTags()}